package string;

public class stringreplacingchardemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s = "welcome to Java";
		System.out.println(s.replace('o', 'a'));
		
		System.out.println(s.replace("Java","Roshan"));
		
		System.out.println(s.replace("welcome", " Rohansa"));
		
		
		

	}

}
